mapper.select:
SELECT A.ROUTE_ID , MIN((SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(A.MASTER_ID, #{companyCd, jdbcType=VARCHAR}, 'ROUTE_CODE', A.ROUTE_ID) FROM DUAL)) AS ROUTE_NAME , A.RESOURCE_ID , MIN(R.RESOURCE_NAME) AS RESOURCE_NAME , A.ITEM_ID , MIN(I.ITEM_NAME) AS ITEM_NAME , MIN(MD.SALES_ORDER_ID) AS SALES_ORDER_ID , MIN(TO_CHAR(DD.DUE_DATE, 'YYYY-MM-DD HH24:MI:SS')) AS DUE_DATE , MIN(TO_CHAR(A.PLAN_DATE, 'YYYY-MM-DD')) AS PLAN_DATE , MIN(A.DEMAND_ID) AS DEMAND_ID , SUM(A.PLAN_QTY) AS PLAN_QTY , MIN(TO_CHAR(MD.START_TIME, 'YYYY-MM-DD HH24:MI:SS')) AS START_TIME , MIN(MD.TOTAL_QTY) AS TOTAL , MIN(MD.LINE_CODE) AS CUSTOMER_LINE , MIN(I.ITEM_GROUP_LARGE) AS ITEM_GROUP_LARGE , MIN(I.ITEM_GROUP_MEDIUM) AS ITEM_GROUP_MEDIUM , MIN(I.ITEM_GROUP_SMALL) AS ITEM_GROUP_SMALL , MIN(I.ITEM_CATEGORY) AS ITEM_CATEGORY , MIN(I.RP_ITEM_ID) AS RP_ITEM_ID , MIN(I.SPECIFICATION) AS SPECIFICATION , MIN(DD.DEMAND_PRIORITY) AS DEMAND_PRIORITY , MIN(R.RESOURCE_GROUP_CD) AS RESOURCE_GROUP_CD , MIN((SELECT MOM_COMMON_PKG.FN_GET_RESOURCE_GROUP_NAME(R.DIVISION_CD, R.COMPANY_CD, R.RESOURCE_GROUP_CD) FROM DUAL)) AS RESOURCE_GROUP_NAME
, ${pivot}
FROM TH_EXP_PRODUCTIONPLANDETAIL A , TH_DPP_NET_DEMAND DD , TH_MST_DEMAND MD , MOM_ITEM_DEFINITION I , MOM_RESOURCE R WHERE A.MASTER_ID = DD.MASTER_ID(+) AND A.PLAN_ID = DD.PLAN_ID(+) AND A.DEMAND_ID = DD.NET_DEMAND_ID(+) AND DD.MASTER_ID = MD.MASTER_ID(+) AND DD.PLAN_ID = MD.PLAN_ID(+) AND DD.DEMAND_ID = MD.DEMAND_ID(+) AND A.MASTER_ID = I.DIVISION_CD AND A.ITEM_ID = I.ITEM_ID AND A.MASTER_ID = R.DIVISION_CD AND A.RESOURCE_ID = R.RESOURCE_CD AND R.OUTSOURCING_FLAG = 'Y' AND A.MASTER_ID = #{divisionCd, jdbcType=VARCHAR} AND A.PLAN_ID = #{planId, jdbcType=VARCHAR} AND A.PLAN_DATE BETWEEN TO_DATE(#{fromDate, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{toDate, jdbcType=VARCHAR}, 'YYYY-MM-DD')
AND UPPER(A.ITEM_ID || '@' || I.ITEM_NAME) LIKE '%' || TRIM(UPPER(#{itemId, jdbcType=VARCHAR})) || '%'
AND A.RESOURCE_ID = #{resourceId, jdbcType=VARCHAR}
GROUP BY A.MASTER_ID , A.PLAN_ID , A.ITEM_ID , A.ROUTE_ID , A.RESOURCE_ID ORDER BY A.MASTER_ID , A.PLAN_ID , A.RESOURCE_ID , A.ROUTE_ID , A.ITEM_ID


		SELECT TO_CHAR(A.PLAN_START_DTTM, 'YYYYMMDD') AS FROM_DATE
             , TO_CHAR(A.PLAN_START_DTTM + A.DEMAND_HORIZON, 'YYYYMMDD') AS TO_DEMAND_DATE
             , TO_CHAR(A.PLAN_START_DTTM + A.PLAN_HORIZON, 'YYYYMMDD') AS TO_PLAN_DATE     
		  FROM TH_MST_PLAN A
		 WHERE MASTER_ID = #{divisionCd, jdbcType=VARCHAR}
		   AND PLAN_ID   = #{planId, jdbcType=VARCHAR}
