mapper.select:
SELECT A.* FROM( SELECT A.IO_CATEGORY , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(A.DIVISION_CD, A.COMPANY_CD, 'IO_CATEGORY', A.IO_CATEGORY ) FROM DUAL) IO_CATEGORY_NAME , A.CUSTOMER_PO_ID , A.SALES_ORDER_ID , A.ORG_CODE , A.SALES_ITEM_ID , A.ITEM_ID , A.ITEM_NAME , A.ORDER_STATE , A.ORDER_STATE_NAME , A.ORDER_QTY , A.SHIP_QTY , A.CANCEL_QTY , A.INOUT_QTY , A.INOUT_CANCEL_QTY , (A.ORDER_QTY - A.SHIP_QTY - A.CANCEL_QTY) AS REMAINED_QTY , A.CONVERSION_UNIT_QTY , TO_CHAR(TRUNC(MOM_COMMON_PKG.FN_GET_LOCAL_TIME(A.DIVISION_CD, A.COMPANY_CD)), 'YYYY-MM-DD') AS CONFIRM_DATE , A.FROM_LOCATION_CD , (SELECT MOM_COMMON_PKG.FN_GET_FACILITY_NAME( A.DIVISION_CD , A.COMPANY_CD , A.FROM_LOCATION_CD) FROM DUAL ) AS FROM_LOCATION_NAME , A.TO_LOCATION_CD , (SELECT MOM_COMMON_PKG.FN_GET_FACILITY_NAME( A.DIVISION_CD , A.COMPANY_CD , A.TO_LOCATION_CD) FROM DUAL ) AS TO_LOCATION_NAME , A.VENDOR_CD , A.VENDOR_NAME , A.DESTINATION_CD , A.DESTINATION_NAME , A.ITEM_STOCK_ID , A.ITEM_STOCK_INOUT_ID , TO_CHAR(A.IO_TIME, 'YYYY-MM-DD') AS IO_TIME , A.DUE_DATE , A.MARKET_CD , A.MARKET_NAME , A.CURRENCY_CD , A.CURRENCY_NAME , A.ITEM_OUTPUT_ID , A.CREATE_BY , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME( A.DIVISION_CD , A.COMPANY_CD , A.CREATE_BY) FROM DUAL) AS CREATE_BY_NAME , A.CREATE_DATE , A.INOUT_PRICE , A.UNIT_PRICE , NVL(A.FOREIGN_TOTAL_PRICE, (A.INOUT_QTY * A.UNIT_PRICE)) AS FOREIGN_AMT , A.ORDER_GUBUN , A.ORDER_GUBUN_NAME , A.DIVISION_CD , A.COMPANY_CD , (SELECT MOM_COMMON_PKG.FN_GET_CURRENT_STOCK( A.DIVISION_CD , A.COMPANY_CD , A.ITEM_ID , A.TO_LOCATION_CD) FROM DUAL ) AS CURRENT_QTY , CASE WHEN A.CURRENCY_CD = A.DEFAULT_CURRENCY THEN 1 ELSE A.SHIP_EXCHANGE_RATE END AS EXCHANGE_RATE , A.PO_NO , A.DEPARTURE_NUMBER , A.DEPARTURE_CANCEL_QTY , A.DEPARTURE_CANCEL_DATE , A.B2BI_CUSTOMER_FLAG , A.ITEM_INPUT_ID AS TRANSACTION_ID , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME( A.DIVISION_CD , A.COMPANY_CD , 'ITEM_GROUP_LARGE' , A.ITEM_GROUP_LARGE) FROM DUAL ) AS ITEM_GROUP_LARGE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME( A.DIVISION_CD , A.COMPANY_CD , 'ITEM_GROUP_MEDIUM' , A.ITEM_GROUP_MEDIUM) FROM DUAL ) AS ITEM_GROUP_MEDIUM , A.DESCRIPTION , A.CONVERSION_UNIT , PD.PRINT_GROUP_ID , TO_CHAR(PD.PRINT_DATE, 'YYYY-MM-DD') AS PRINT_DATE , A.SPECIFICATION , A.HS_PART_NO , A.LGE_PART_NO FROM (SELECT I.IO_CATEGORY , O.CUSTOMER_PO_ID , O.SALES_ORDER_ID , O.ORG_CODE , O.SALES_ITEM_ID , O.ITEM_ID , O.ITEM_NAME , O.ORDER_QTY , NVL(O.SHIP_QTY, 0) AS SHIP_QTY , NVL(O.CANCEL_QTY, 0) AS CANCEL_QTY , I.QTY AS INOUT_QTY , I.PREV_LOCATION_CD AS FROM_LOCATION_CD , I.LOCATION_CD AS TO_LOCATION_CD , O.VENDOR_CD , O.VENDOR_NAME , O.DESTINATION_CD , O.DESTINATION_NAME , I.ITEM_STOCK_ID , I.ITEM_STOCK_INOUT_ID , I.IO_TIME , DECODE(O.CURRENCY_CD, P.CURRENCY_CD, I.UNIT_PRICE, I.FOREIGN_UNIT_PRICE) AS UNIT_PRICE , O.MARKET_CD , O.MARKET_NAME , O.CURRENCY_CD , O.CURRENCY_NAME , I.ITEM_OUTPUT_ID , I.CREATE_BY , TO_CHAR(I.CREATE_DATE, 'YYYY-MM-DD HH24:MI:SS') AS CREATE_DATE , I.INOUT_PRICE , I.EXCHANGE_RATE AS SHIP_EXCHANGE_RATE , O.ORDER_STATE , O.ORDER_STATE_NAME , TO_CHAR(O.DUE_DATE, 'YYYY-MM-DD') AS DUE_DATE , O.ORDER_GUBUN , O.ORDER_GUBUN_NAME , O.DIVISION_CD , O.COMPANY_CD , P.CURRENCY_CD AS DEFAULT_CURRENCY , O.CUSTOMER_PO_NO AS PO_NO , I.ITEM_DEPARTURE_ID AS DEPARTURE_NUMBER , DC.DEPARTURE_CANCEL_QTY , DC.DEPARTURE_CANCEL_DATE , (SELECT MOM_COMMON_PKG.FN_GET_B2BI_CUSTOMER_FLAG( O.DIVISION_CD , O.COMPANY_CD , O.VENDOR_CD) FROM DUAL ) AS B2BI_CUSTOMER_FLAG , I.ITEM_INPUT_ID , I.FOREIGN_TOTAL_PRICE , MI.ITEM_GROUP_LARGE , MI.ITEM_GROUP_MEDIUM , I.DESCRIPTION , I.CONVERSION_UNIT_QTY , MI.CONVERSION_UNIT , (SELECT SUM(QTY) FROM MOM_ITEM_STOCK_INOUT WHERE DIVISION_CD = I.DIVISION_CD AND COMPANY_CD = I.COMPANY_CD AND IO_TYPE = I.IO_TYPE AND PREV_STOCK_INOUT_ID = I.ITEM_STOCK_INOUT_ID) AS INOUT_CANCEL_QTY , MI.SPECIFICATION , O.HS_PART_NO , O.LGE_PART_NO FROM MOM_SALES_ORDER_V O , MOM_ITEM_STOCK_INOUT I , MOM_PARAMETER P , MOM_B2BI_DEPARTURE_CANCEL_V DC , MOM_ITEM_DEFINITION MI WHERE O.DIVISION_CD = I.DIVISION_CD AND O.COMPANY_CD = I.COMPANY_CD AND O.SALES_ORDER_ID = I.ITEM_ORDER_ID AND O.DIVISION_CD = P.DIVISION_CD AND O.COMPANY_CD = P.COMPANY_CD AND I.DIVISION_CD = DC.DIVISION_CD(+) AND I.PO_NO = DC.PO_NO(+) AND I.ITEM_INPUT_ID = DC.TRANSACTION_ID(+) AND I.DIVISION_CD = MI.DIVISION_CD(+) AND I.COMPANY_CD = MI.COMPANY_CD(+) AND I.ITEM_ID = MI.ITEM_ID(+) AND I.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND I.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND I.IO_TYPE = 'I' AND I.IO_CATEGORY IN ('S001','S002')
AND ( O.ITEM_ID LIKE '%' || TRIM(UPPER(#{itemName, jdbcType=VARCHAR})) || '%' OR UPPER(O.ITEM_NAME) LIKE '%' || TRIM(UPPER(#{itemName, jdbcType=VARCHAR})) || '%' )
AND UPPER(O.CUSTOMER_PO_ID) LIKE '%' || TRIM(UPPER(#{customerPoId, jdbcType=VARCHAR})) || '%'
AND UPPER(O.CUSTOMER_PO_NO) LIKE '%' || TRIM(UPPER(#{customerPoNo, jdbcType=VARCHAR})) || '%'
AND UPPER(I.ITEM_DEPARTURE_ID) LIKE '%' || TRIM(UPPER(#{departureNo, jdbcType=VARCHAR})) || '%'
AND O.ORDER_STATE = #{orderState, jdbcType=VARCHAR}
AND O.CURRENCY_CD = #{currencyName, jdbcType=VARCHAR}
AND O.MARKET_CD = #{marketName, jdbcType=VARCHAR}
AND A.ORG_CODE = #{organizationCode, jdbcType=VARCHAR}
AND I.IO_TIME BETWEEN TO_DATE(#{fromDate, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{toDate, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 23.9997/24 UNION ALL SELECT I.IO_CATEGORY , O.CUSTOMER_PO_ID , O.SALES_ORDER_ID , O.ORG_CODE , O.SALES_ITEM_ID , O.ITEM_ID , O.ITEM_NAME , O.ORDER_QTY , NVL(O.SHIP_QTY, 0) AS SHIP_QTY , NVL(O.CANCEL_QTY, 0) AS CANCEL_QTY , I.QTY AS INOUT_QTY , I.PREV_LOCATION_CD AS FROM_LOCATION_CD , I.LOCATION_CD AS TO_LOCATION_CD , O.VENDOR_CD , O.VENDOR_NAME , O.DESTINATION_CD , O.DESTINATION_NAME , I.ITEM_STOCK_ID , I.ITEM_STOCK_INOUT_ID , I.IO_TIME , DECODE(O.CURRENCY_CD, P.CURRENCY_CD, I.UNIT_PRICE, I.FOREIGN_UNIT_PRICE) AS UNIT_PRICE , O.MARKET_CD , O.MARKET_NAME , O.CURRENCY_CD , O.CURRENCY_NAME , I.ITEM_OUTPUT_ID , I.CREATE_BY , TO_CHAR(I.CREATE_DATE, 'YYYY-MM-DD HH24:MI:SS') AS CREATE_DATE , I.INOUT_PRICE , I.EXCHANGE_RATE AS SHIP_EXCHANGE_RATE , O.ORDER_STATE , O.ORDER_STATE_NAME , TO_CHAR(O.DUE_DATE, 'YYYY-MM-DD') AS DUE_DATE , O.ORDER_GUBUN , O.ORDER_GUBUN_NAME , O.DIVISION_CD , O.COMPANY_CD , P.CURRENCY_CD AS DEFAULT_CURRENCY , O.CUSTOMER_PO_NO AS PO_NO , I.ITEM_DEPARTURE_ID AS DEPARTURE_NUMBER , 0 AS DEPARTURE_CANCEL_QTY , NULL AS DEPARTURE_CANCEL_DATE , 'N' AS B2BI_CUSTOMER_FLAG , I.ITEM_INPUT_ID , I.FOREIGN_TOTAL_PRICE , MI.ITEM_GROUP_LARGE , MI.ITEM_GROUP_MEDIUM , I.DESCRIPTION , I.CONVERSION_UNIT_QTY , MI.CONVERSION_UNIT , 0 AS INOUT_CANCEL_QTY , MI.SPECIFICATION , O.HS_PART_NO , O.LGE_PART_NO FROM MOM_SALES_ORDER_V O , MOM_ITEM_STOCK_INOUT I , MOM_PARAMETER P , MOM_ITEM_DEFINITION MI WHERE O.DIVISION_CD = I.DIVISION_CD AND O.COMPANY_CD = I.COMPANY_CD AND O.SALES_ORDER_ID = I.ITEM_ORDER_ID AND O.DIVISION_CD = P.DIVISION_CD AND O.COMPANY_CD = P.COMPANY_CD AND I.DIVISION_CD = MI.DIVISION_CD(+) AND I.COMPANY_CD = MI.COMPANY_CD(+) AND I.ITEM_ID = MI.ITEM_ID(+) AND I.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND I.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND I.IO_TYPE = 'O' AND I.IO_CATEGORY = 'PRTN001'
AND ( O.ITEM_ID LIKE '%' || TRIM(UPPER(#{itemName, jdbcType=VARCHAR})) || '%' OR UPPER(O.ITEM_NAME) LIKE '%' || TRIM(UPPER(#{itemName, jdbcType=VARCHAR})) || '%' )
AND UPPER(O.CUSTOMER_PO_ID) LIKE '%' || TRIM(UPPER(#{customerPoId, jdbcType=VARCHAR})) || '%'
AND UPPER(O.CUSTOMER_PO_NO) LIKE '%' || TRIM(UPPER(#{customerPoNo, jdbcType=VARCHAR})) || '%'
AND UPPER(I.ITEM_DEPARTURE_ID) LIKE '%' || TRIM(UPPER(#{departureNo, jdbcType=VARCHAR})) || '%'
AND O.ORDER_STATE = #{orderState, jdbcType=VARCHAR}
AND O.CURRENCY_CD = #{currencyName, jdbcType=VARCHAR}
AND O.MARKET_CD = #{marketName, jdbcType=VARCHAR}
AND A.ORG_CODE = #{organizationCode, jdbcType=VARCHAR}
AND I.IO_TIME BETWEEN TO_DATE(#{fromDate, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{toDate, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 23.9997/24 AND NOT EXISTS (SELECT '*' FROM MOM_ITEM_STOCK_INOUT IC WHERE IC.DIVISION_CD = I.DIVISION_CD AND IC.COMPANY_CD = I.COMPANY_CD AND IC.PREV_STOCK_INOUT_ID = I.ITEM_STOCK_INOUT_ID AND IC.IO_TYPE = I.IO_TYPE AND IC.IO_CATEGORY = 'PRTNC001' ) ) A , MOM_PRINT_DOC PD WHERE 1=1 AND A.DIVISION_CD = PD.DIVISION_CD(+) AND A.COMPANY_CD = PD.COMPANY_CD(+) AND A.ITEM_STOCK_INOUT_ID = PD.ITEM_STOCK_INOUT_ID(+) ) A WHERE 1 = 1
AND A.VENDOR_CD = #{searchKeyword, jdbcType=VARCHAR}
AND A.DESTINATION_CD = #{searchKeyword, jdbcType=VARCHAR}
AND A.TO_LOCATION_CD = #{locationName, jdbcType=VARCHAR}
AND NVL(A.DEPARTURE_CANCEL_QTY, 0) = 0
AND A.DEPARTURE_CANCEL_QTY > 0
AND UPPER(A.CREATE_BY_NAME) LIKE '%' || TRIM(UPPER(#{createByName, jdbcType=VARCHAR})) || '%'
ORDER BY A.DUE_DATE, A.CUSTOMER_PO_ID, A.SALES_ORDER_ID

mapper.insert:
	{
        CALL P_CANCEL_SHIP_RESULT(
        	#{p_err_code , mode=OUT, jdbcType=VARCHAR}
           ,#{p_err_msg, mode=OUT, jdbcType=VARCHAR}
           ,#{divisionCd, mode=IN, jdbcType=VARCHAR}
           ,#{companyCd, mode=IN, jdbcType=VARCHAR}
           ,#{itemStockInoutId, mode=IN, jdbcType=VARCHAR}
           ,#{ioTime, mode=IN, jdbcType=VARCHAR}
           ,#{orderType, mode=IN, jdbcType=VARCHAR}
           ,#{updateBy, mode=IN, jdbcType=VARCHAR}
        )
    }

mapper.delete:
DELETE FROM MOM_PRINT_DOC_TMP
 WHERE DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
   AND COMPANY_CD = #{companyCd, jdbcType=VARCHAR}
   AND MENU_ID = #{menuId, jdbcType=VARCHAR}

mapper.insert:
		CALL P_COMMON_PRINT_DOC (
        	#{p_err_code, mode=OUT, jdbcType=VARCHAR}
          , #{p_err_msg, mode=OUT, jdbcType=VARCHAR}
          , #{divisionCd, mode=IN, jdbcType=VARCHAR}
          , #{companyCd, mode=IN, jdbcType=VARCHAR}
          , #{menuId, mode=IN, jdbcType=VARCHAR}
          , #{createBy, mode=IN, jdbcType=VARCHAR}
        )
