mapper.select:
SELECT (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(#{divisionCd, jdbcType=VARCHAR}, #{companyCd, jdbcType=VARCHAR},'ITEM_TYPE', B.ITEM_TYPE) FROM DUAL) AS ITEM_TYPE_NAME , A.ITEM_ID , B.ITEM_NAME AS ITEM_NAME , B.SPECIFICATION SPECIFICATION , B.UNIT UNIT , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(#{divisionCd, jdbcType=VARCHAR}, #{companyCd, jdbcType=VARCHAR},'ITEM_UNIT', B.UNIT) FROM DUAL) AS UNIT_NAME , A.UNIT_PRICE , NVL(LEAD(A.UNIT_PRICE) OVER(PARTITION BY A.ITEM_ID, A.PRICE_TYPE ORDER BY A.ITEM_ID, A.PRICE_TYPE, A.START_DATE DESC), 0) AS PREV_PRICE , TO_CHAR(A.START_DATE, 'YYYY-MM-DD') AS START_DATE , TO_CHAR(A.END_DATE, 'YYYY-MM-DD') AS END_DATE , A.DESCRIPTION , A.USE_YN , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(#{divisionCd, jdbcType=VARCHAR}, #{companyCd, jdbcType=VARCHAR},'USE_FLAG', A.USE_YN) FROM DUAL) AS USE_YN_NAME , A.UPDATE_BY , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME (#{divisionCd, jdbcType=VARCHAR}, #{companyCd, jdbcType=VARCHAR}, A.UPDATE_BY) FROM DUAL) AS UPDATE_BY_NAME , TO_CHAR(A.UPDATE_DATE, 'YYYY-MM-DD') AS UPDATE_DATE , A.PRICE_TYPE FROM MOM_STANDARD_PRICE A , MOM_ITEM_DEFINITION B WHERE 1 = 1 AND A.DIVISION_CD = B.DIVISION_CD AND A.COMPANY_CD = B.COMPANY_CD AND A.ITEM_ID = B.ITEM_ID AND A.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND A.COMPANY_CD = #{companyCd, jdbcType=VARCHAR}
AND (UPPER(A.ITEM_ID) LIKE UPPER('%' || #{itemName, jdbcType=VARCHAR} || '%') OR UPPER(B.ITEM_NAME) LIKE UPPER('%' || #{itemName, jdbcType=VARCHAR} || '%'))
AND B.ITEM_TYPE = #{itemType, jdbcType=VARCHAR}
AND TO_DATE(#{fromDate, jdbcType=VARCHAR}, 'YYYY-MM-DD') BETWEEN A.START_DATE AND A.END_DATE

mapper.delete:
DELETE 
  FROM MOM_STANDARD_PRICE_TMP
 WHERE DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
   AND COMPANY_CD = #{companyCd, jdbcType=VARCHAR}

mapper.select:
SELECT *
  FROM MOM_STANDARD_PRICE_TMP
 WHERE DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
   AND COMPANY_CD = #{companyCd, jdbcType=VARCHAR}
   AND CUD_FLAG = #{cudFlag, jdbcType=VARCHAR}

mapper.update:
UPDATE MOM_STANDARD_PRICE_TMP
SET 
	UNIT_PRICE 		= #{unitPrice, jdbcType=INTEGER}
WHERE 
	    DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
	AND COMPANY_CD  = #{companyCd, jdbcType=VARCHAR}
	AND ITEM_ID		= UPPER(TRIM(#{itemId, jdbcType=VARCHAR}))
	AND TO_CHAR(START_DATE, 'YYYY-MM-DD') = #{startDate, jdbcType=VARCHAR}
	AND CUD_FLAG	= #{cudFlag, jdbcType=VARCHAR}

mapper.insert:
	{
        CALL P_STANDARD_PRICE_UPSERT (
             #{p_err_code, mode=OUT, jdbcType=VARCHAR}
           , #{p_err_msg, mode=OUT, jdbcType=VARCHAR}
           , #{divisionCd, mode=IN, jdbcType=VARCHAR}
           , #{companyCd, mode=IN, jdbcType=VARCHAR}
           , #{cudFlag, mode=IN, jdbcType=VARCHAR}
           , #{priceType, mode=IN, jdbcType=VARCHAR}
        )
    }
