mapper.select:
SELECT SHIFT_ID , RESOURCE_CD , RESOURCE_NAME , SHIFT_CD , SHIFT_CODE_NAME , SEQ AS SEQ , DECODE (SEQ, 1, #{MESSAGES30065, jdbcType=VARCHAR}, 2, #{MESSAGES30067, jdbcType=VARCHAR}, 3, #{MESSAGES30066, jdbcType=VARCHAR}) AS SEQ_NM
, ${pivot}
FROM (SELECT A.RESOURCE_CD , (SELECT MOM_COMMON_PKG.FN_GET_RESOURCE_NAME (A.DIVISION_CD, A.COMPANY_CD, A.RESOURCE_CD) FROM DUAL) AS RESOURCE_NAME , B.SHIFT_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME (A.DIVISION_CD , A.COMPANY_CD , 'SHIFT_CODE' , B.SHIFT_CD) FROM DUAL) AS SHIFT_CODE_NAME , SUBSTR (A.START_TIME, 1, 6) AS DAY , TO_CHAR (A.APPLY_DATE, 'YYYY-MM-DD') AS APPLY_DATE , 1 AS SEQ , A.SHIFT_ID FROM MOM_SHIFT_SCHEDULE A , MOM_SHIFT B WHERE A.DIVISION_CD = B.DIVISION_CD AND A.COMPANY_CD = B.COMPANY_CD AND A.SHIFT_ID = B.SHIFT_ID AND A.RESOURCE_CD = B.RESOURCE_CD AND A.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND A.COMPANY_CD = #{companyCd, jdbcType=VARCHAR}
AND TO_CHAR(A.APPLY_DATE, 'YYYY-MM-DD') IN(${pivot2})
AND B.SHIFT_CD = #{shiftCd, jdbcType=VARCHAR}
AND A.RESOURCE_CD = #{resourceCd, jdbcType=VARCHAR}
UNION ALL SELECT A.RESOURCE_CD , '' AS RESOURCE_NAME , B.SHIFT_CD , '' AS SHIFT_CODE_NAME , SUBSTR (A.END_TIME, 1, 6) AS DAY , TO_CHAR (A.APPLY_DATE, 'YYYY-MM-DD') AS APPLY_DATE , 2 AS SEQ , A.SHIFT_ID FROM MOM_SHIFT_SCHEDULE A , MOM_SHIFT B WHERE A.DIVISION_CD = B.DIVISION_CD AND A.COMPANY_CD = B.COMPANY_CD AND A.SHIFT_ID = B.SHIFT_ID AND A.RESOURCE_CD = B.RESOURCE_CD AND A.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND A.COMPANY_CD = #{companyCd, jdbcType=VARCHAR}
AND TO_CHAR(A.APPLY_DATE, 'YYYY-MM-DD') IN(${pivot2})
AND B.SHIFT_CD = #{shiftCd, jdbcType=VARCHAR}
AND A.RESOURCE_CD = #{resourceCd, jdbcType=VARCHAR}
UNION ALL SELECT A.RESOURCE_CD , '' AS RESOURCE_NAME , B.SHIFT_CD , '' AS SHIFT_CODE_NAME , TO_CHAR (A.WORK_PERSON_CNT) AS DAY , TO_CHAR (A.APPLY_DATE, 'YYYY-MM-DD') AS APPLY_DATE , 3 AS SEQ , A.SHIFT_ID FROM MOM_SHIFT_SCHEDULE A , MOM_SHIFT B WHERE A.DIVISION_CD = B.DIVISION_CD AND A.COMPANY_CD = B.COMPANY_CD AND A.SHIFT_ID = B.SHIFT_ID AND A.RESOURCE_CD = B.RESOURCE_CD AND A.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND A.COMPANY_CD = #{companyCd, jdbcType=VARCHAR}
AND TO_CHAR(A.APPLY_DATE, 'YYYY-MM-DD') IN(${pivot2})
AND B.SHIFT_CD = #{shiftCd, jdbcType=VARCHAR}
AND A.RESOURCE_CD = #{resourceCd, jdbcType=VARCHAR}
) GROUP BY SHIFT_ID , RESOURCE_CD , RESOURCE_NAME , SHIFT_CD , SHIFT_CODE_NAME , SEQ ORDER BY RESOURCE_CD, SHIFT_CD, SEQ

mapper.update:
	UPDATE MOM_SHIFT_SCHEDULE
	SET



			 START_TIME = #{value, jdbcType=VARCHAR},


			 END_TIME = #{value, jdbcType=VARCHAR},


			 WORK_PERSON_CNT = #{value, jdbcType=INTEGER},



	UPDATE_BY = #{updateBy, jdbcType=VARCHAR},
	UPDATE_DATE = MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{divisionCd, jdbcType=VARCHAR}, #{companyCd, jdbcType=VARCHAR})
	WHERE DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
	  AND COMPANY_CD = #{companyCd, jdbcType=VARCHAR}
	  AND SHIFT_ID = #{shiftId, jdbcType=VARCHAR}
      AND RESOURCE_CD = #{resourceCd, jdbcType=VARCHAR}
      AND APPLY_DATE = TO_DATE(#{applyDate, jdbcType=VARCHAR}, 'YYYY-MM-DD')

mapper.delete:
 DELETE MOM_SHIFT_SCHEDULE
	WHERE DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
	  AND COMPANY_CD = #{companyCd, jdbcType=VARCHAR}
	  AND SHIFT_ID = #{shiftId, jdbcType=VARCHAR}
      AND RESOURCE_CD = #{resourceCd, jdbcType=VARCHAR}
      AND APPLY_DATE = TO_DATE(#{applyDate, jdbcType=VARCHAR}, 'YYYY-MM-DD')
