mapper.select:
SELECT DIVISION_CD , COMPANY_CD , USER_ID AS USER_ID_TMP , PASSWORD , EMP_NO , NAME , NAME_ENG , POSITION , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(#{divisionCd, jdbcType=VARCHAR}, #{companyCd, jdbcType=VARCHAR}, 'POSITION', POSITION) FROM DUAL) AS POSITION_NAME , EMAIL , CELL_PHONE_NO , OFFICE_PHONE_NO , FAX_NO , USER_TYPE , MANAGER_YN , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(#{divisionCd, jdbcType=VARCHAR}, #{companyCd, jdbcType=VARCHAR}, 'N_Y', MANAGER_YN) FROM DUAL) AS MANAGER_YN_NAME , DEFAULT_LANGUAGE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(#{divisionCd, jdbcType=VARCHAR}, #{companyCd, jdbcType=VARCHAR}, 'LANGUAGE_CODE', DEFAULT_LANGUAGE) FROM DUAL) AS DEFAULT_LANGUAGE_NAME , DEPT_CD , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(#{divisionCd, jdbcType=VARCHAR}, #{companyCd, jdbcType=VARCHAR}, 'DEPT_CD', DEPT_CD) FROM DUAL) AS DEPT_NAME , EMP_AUTHORITY , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(#{divisionCd, jdbcType=VARCHAR}, #{companyCd, jdbcType=VARCHAR}, 'EMP_AUTHORITY', EMP_AUTHORITY) FROM DUAL) AS EMP_AUTHORITY_NAME , USE_YN , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(#{divisionCd, jdbcType=VARCHAR}, #{companyCd, jdbcType=VARCHAR}, 'USE_FLAG', USE_YN) FROM DUAL) AS USE_YN_NAME , DESCRIPTION , TO_CHAR(CREATE_DATE, 'YYYY-MM-DD') AS CREATE_DATE , CREATE_BY , TO_CHAR(UPDATE_DATE, 'YYYY-MM-DD') AS UPDATE_DATE , UPDATE_BY , LOCK_STATUS , PWERR_CN , NVL(TO_CHAR(PWCHG_DT, 'YYYY-MM-DD'), '0000-00-00') AS PWCHG_DT , NVL(TO_CHAR(LAST_LOGON_DT, 'YYYY-MM-DD HH24:MI:SS'), '0000-00-00 00:00:00') AS LAST_LOGON_DT , VENDOR_CD , (SELECT MOM_COMMON_PKG.FN_GET_VENDOR_NAME(#{divisionCd, jdbcType=VARCHAR}, #{companyCd, jdbcType=VARCHAR}, VENDOR_CD) FROM DUAL) AS VENDOR_NAME FROM TU_USER
AND DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND COMPANY_CD = #{companyCd, jdbcType=VARCHAR}
AND EMP_NO LIKE '%' || UPPER(#{empNo, jdbcType=VARCHAR}) || '%'
AND NAME LIKE '%' || UPPER(#{empName, jdbcType=VARCHAR}) || '%'
AND DEPT_CD = #{partName, jdbcType=VARCHAR}
ORDER BY DIVISION_CD, COMPANY_CD, NAME ASC

mapper.update:
UPDATE TU_USER
SET PASSWORD = #{password, jdbcType=VARCHAR}             
  , EMP_NO = #{empNo, jdbcType=VARCHAR}           
  , NAME = #{name, jdbcType=VARCHAR}               
  , NAME_ENG = #{nameEng, jdbcType=VARCHAR}           
  , POSITION = #{position, jdbcType=VARCHAR}          
  , EMAIL = #{email, jdbcType=VARCHAR}             
  , CELL_PHONE_NO = #{cellPhoneNo, jdbcType=VARCHAR}      
  , OFFICE_PHONE_NO = #{officePhoneNo, jdbcType=VARCHAR}    
  , FAX_NO = #{faxNo, jdbcType=VARCHAR}             
  , USER_TYPE = #{userType, jdbcType=VARCHAR}          
  , MANAGER_YN = #{managerYn, jdbcType=VARCHAR}         
  , DEFAULT_LANGUAGE = #{defaultLanguage, jdbcType=VARCHAR}   
  , DEPT_CD = #{deptCd, jdbcType=VARCHAR}            
  , EMP_AUTHORITY = #{empAuthority, jdbcType=VARCHAR}      
  , USE_YN = #{useYn, jdbcType=VARCHAR}             
  , DESCRIPTION = #{description, jdbcType=VARCHAR}        
  , UPDATE_DATE = MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{divisionCd, jdbcType=VARCHAR}, #{companyCd, jdbcType=VARCHAR})
  , UPDATE_BY = #{updateBy, jdbcType=VARCHAR}      
  , PWCHG_DT = MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{divisionCd, jdbcType=VARCHAR}, #{companyCd, jdbcType=VARCHAR})
  , VENDOR_CD = #{vendorCd, jdbcType=VARCHAR}
WHERE DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND COMPANY_CD = #{companyCd, jdbcType=VARCHAR}
AND USER_ID = #{userIdTmp, jdbcType=VARCHAR}

UPDATE TU_USER
	SET PASSWORD = #{newPassword, jdbcType=VARCHAR}
WHERE DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
AND COMPANY_CD = #{companyCd, jdbcType=VARCHAR}
AND USER_ID = #{userId, jdbcType=VARCHAR}
AND PASSWORD = #{nowPassword, jdbcType=VARCHAR}

mapper.delete:
	DELETE FROM TU_USER
	WHERE  DIVISION_CD = #{divisionCd, jdbcType=VARCHAR}
    AND COMPANY_CD = #{companyCd, jdbcType=VARCHAR}
	AND USER_ID = #{userIdTmp, jdbcType=VARCHAR}
